import axios from "axios";
import { PRODUCT_DETAILS_REQUEST, PRODUCT_DETAILS_SUCCESS, PRODUCT_LIST_FAIL, PRODUCT_LIST_REQUEST, PRODUCT_LIST_SUCCESS } from "../constants/productConstants"

export const listProducts = () => async (dispatch) => {
    dispatch({
        type: PRODUCT_LIST_REQUEST
    })
    try {
        const { data } = await axios.get('/api/products');
        dispatch({
            type: PRODUCT_LIST_SUCCESS,
            payload: data
        })
    } catch (error) {
        dispatch({
            type: PRODUCT_LIST_FAIL,
            payload: error.message
        })
    }
};


export const detailsProducts = (productId) => async (dispatch) => {
    dispatch({ type: PRODUCT_DETAILS_REQUEST, playload: productId });
    try {
        const { data } = await axios.get(`/api/products/${productId}`);
        dispatch({ type: PRODUCT_DETAILS_SUCCESS, playload: data });
    } catch (error) {
        dispatch({
            type: PRODUCT_LIST_FAIL,
            playload: error.response && error.response.data.message
                ? error.response.data.message
                : error.response
        });
    }
}
