import React, { useEffect } from "react";
import { useDispatch, useSelector } from 'react-redux';
import { Link } from "react-router-dom";
import { addToCart, removeFromCart } from "../actions/cartActions";
import MessageBox from '../components/MessageBox';

export default function CartScreen(props) {
    const productId = props.match.params.id;
    const qty = props.location.search ? Number(props.location.search.split('=')[1]) : 1;

    const cart = useSelector((state) => state.cart);
    const { cartItems } = cart;


    const dispatch = useDispatch();
    useEffect(() => {
        if (productId) {
            dispatch(addToCart(productId, qty))
        }
    }, [dispatch, productId, qty]);


    const removeFromCartHandler = (id) => {
        //dispatch(removeFromCart(id));
    }

    const checkoutHandle = () => {
        props.history.push('/signin?redirect=shopping');
    }

    return (
        <div className="row top">
            <div className="col-2">
                <h1>Shopping Cart</h1>
                {
                    cartItems.length === 0 ?
                        <MessageBox>Car is empty.<Link to="/"> Go Shopping</Link></MessageBox> :
                        (<ul>
                            {cartItems.map((item) => (
                                <li key={item.product}>
                                    <div className="row">
                                        <div>
                                            <img className="small" src={item.image} alt={item.name}></img>
                                        </div>
                                        <div className="min-30">
                                            <Link to={`/product/${item.product}`}>{item.name}</Link>
                                        </div>
                                        <div>
                                            <select
                                                value={item.qty}
                                                onChange={e => dispatch(addToCart(item.product), Number(e.target.value))}>
                                                {[...Array(item.countInStock).keys()].map((x) => (
                                                    <option key={x + 1} value={x + 1}> {x + 1}</option>
                                                ))}
                                            </select>

                                        </div>
                                        <div>${item.price}</div>
                                        <div><button type="button" onClick={removeFromCartHandler(item.product)}>Delete</button></div>
                                    </div>
                                </li>
                            ))}
                        </ul>)
                }
            </div>
            <div className="col-1">
                <div className="cart cart-boby">
                    <ul>
                        <il>
                            {console.log(cartItems.length)}
                            <h2>Subtotal ({cartItems.reduce((a, c) => a + c.qty, 0)} items) : B {cartItems.reduce((a, c) => a + c.price * c.qty, 0)} </h2>
                        </il>
                        <il>
                            <button type="button" onClick={checkoutHandle} className="primary block" disabled={cartItems.length === 0}>CheckOut</button>
                        </il>
                    </ul>

                </div>
            </div>
        </div>
    );
}
